import React,{useState} from 'react'
const SearchFilter = ({location, onNameFilter, beds, onLocationFilter, onBedFilter}) => {
    // const[name, setName] = useState("");
    // const[date, setDate] = useState("");
    // const[price, setPrice] = useState("");
    // const[loc, setLocation] = useState("");
    // const[beds, setBeds] = useState("");

    const[filters, setFilters] = useState({
        name: '', date: '', price: '', loc: '', bd: ''
    });


    const handleInput = (field) => (event) => {
        const{value} =  event.target;   

        setFilters({
            ...filters,
            [field]: value
        });

        switch(field){
            case "name":
                // setName(value);
                onNameFilter(value);
                break;
            case "date":
                // setDate(value); 
                break;
            case "price":
                // setPrice(value); 
                break;
            case "loc":
                // setLocation(value);
                onLocationFilter(value); 
                break;
            case "bd":
                // setBeds(value); 
                onBedFilter(value);
                break;
            default:
                break;
        }
    };

    // console.log(location);
    return (
        <div className="row my-5">
            <div className="col">
                <h4 className="bottom-bottom">Filters</h4>
            </div>
            <div className="col-sm-12 my-2">
                <label htmlFor="name">Name</label>
                <input 
                    type="text" 
                    className="form-control"
                    id="name" 
                    value={filters.name}
                    onChange={handleInput("name")}/> 
            </div>

            <div className="col-sm-12 my-2">
                <label htmlFor="date">When to move in</label>
                <input type="date" className="form-control" id="date" onChange={handleInput("date")}/>
            </div>

            <div className="col-sm-12 my-2">
                <label htmlFor="price">Price</label>
                <select className="form-control" id="price" onChange={handleInput("price")}>
                        <option value="">-Select-</option>
                </select>                 
            </div>

            <div className="col-sm-12 my-2">
                <label htmlFor="bed">Beds</label>
                <select className="form-control" id="bed" onChange={handleInput("bd")}>
                        <option value="">-Select-</option>
                        {beds.map((bd) => {
                            return (
                                <option value={bd} key={bd}>
                                    {bd}
                                </option>
                            );
                        })}
                </select>                 
            </div>

            <div className="col-sm-12 my-2">
                <label htmlFor="location">Location</label>
                <select className="form-control" id="loc" onChange={handleInput("loc")}>
                        <option value="">-Select-</option>
                        {location.map((loc) => {
                            return (
                                <option value={loc} key={loc}>
                                    {loc}
                                </option>
                            );
                        })}
                </select>                 
            </div>
        </div>
    )
}

export default SearchFilter;
